package com.java.practice;

public class RoundNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float i=11.8f;
		System.out.println(Math.round(i));
		int n=4851;
		int a=(n/10)*10;
		int b=a+10;
		System.out.println("Second- "+b);
		System.out.println("First- "+a);
		
	}

}
